import java.nio.charset.*
import groovy.json.JsonOutput
class Obj{
   private String PLANT_ID;
   private String VERSION_INFO;
   private String PART_NO;
   private String REQ_DATE;
   private String REQ_TIME;
   private String REQ_HOUR;
   private String SHIFT_NO;
   private String DIMENSION_TYPE;
   private String REQ_QTY;
   private String PATH;
   void setPLANT_ID(String id){
      this.PLANT_ID = id;
   }
   void setVERSION_INFO(String info){
      this.VERSION_INFO=info;
   }
   void setPART_NO(String partNo){
      this.PART_NO=partNo;
