def flowFile = session.get();
if (flowFile == null) {
    return;
}
def path = flowFile.getAttribute("path");
def filename = flowFile.getAttribute("filename");
flowFile = session.write(flowFile,{ inputStream, outputStream ->
	def text = IOUtils.toString(inputStream,StandardCharsets.UTF_8)
	String [] arr = text.split("\n")
	def list =[]
	for(String string : arr){
	   string = string.trim();
	   Obj obj = new Obj();
	   obj.setPLANT_ID(string[0..3]);
	   obj.setPART_NO(string[4..21].trim());
	   obj.setREQ_DATE(string[22..29]);
	   obj.setREQ_TIME(string[30..35]);
	   obj.setREQ_HOUR(string[30..31]);
	   obj.setSHIFT_NO(string[36..36]);
	   obj.setDIMENSION_TYPE(string[37..37]);
	   obj.setPATH(path+"/"+filename);
	   obj.setREQ_QTY(string[38..-1])
	   obj.setVERSION_INFO(filename.split("_")[0][-8..-1]);
	   list.add(obj);
	}
	def jsonList = JsonOutput.toJson(list);
	outputStream.write(jsonList.getBytes(StandardCharsets.UTF_8)) } as StreamCallback)
session.transfer(flowFile, ExecuteScript.REL_SUCCESS);
